@register(outgoing=True, pattern="^[.]purge$")
async def Purge(e):
  if e.is_reply:
    chat = await e.get_input_chat()
    msgs = []
    count = 0
    async for msg in e.client.iter_messages(chat, min_id=e.reply_to_msg_id):
      msgs.append(msg)
      count += 1
      msgs.append(e.reply_to_msg_id)
      if len(msgs) == 100:
        await e.client.delete_messages(chat, msgs)
        msgs = []
    if msgs.__len__() > 0:
      await e.client.delete_messages(chat, msgs)
  else:
    await e.edit("**тЭМ Rispondere al messaggio dal quale iniziare a eliminare тЭМ**")

